            .AddCase(() => accessor.Invoke(instance, 1), "MethodAccessor.Invoke")
            .AddCase(() => accessor.InvokeInstanceFunction<TestClass, int, int>(instance, 1), "MethodAccessor.InvokeInstanceFunction<,,>")
            .DoTest()
